import React from 'react';
import "./App.css"; 


//we can only use className coz this is jsx
//we add props to componet in {props.name} or {prop.id} so it is rendered
// by the wen tweet function return jsx/html

function Tweet(props){
    return(
        <div  className="tweet" > 
            <h2>{props.name}</h2>
            <p>{props.message}</p>
            <h3>{props.likes} <span> Likes</span></h3> 
        </div>
    )
}

export default Tweet;
