import React, {useState} from 'react';
import Tweet from './Tweet';



// const counter = 8; this is just like a variable that doesnt change

//this below is our component that returns JSX to index.html 
function App(){

    //this is the state at which our app will be invoked
    //it can be a boolean, a string or int just anything of choice
    const [isRed, setRed]= useState(False);
    const [count, setCount] = useState(0);

    return(
        <div className="app">
            <button>Increment</button>
            <h1>{count}</h1>


            <Tweet name ="Dev Bere" message=" I am learning react day 1/100 #100daysofcode" likes="100"/>
            <Tweet name ="Jon Bere" message=" I am king in the North" likes="100"/>
            <Tweet name ="React Donga" message=" This message is being displayed as a Prop in Twitter.js" likes="100"/>
            <Tweet name ="Jon Snow" message=" Twitter.js has component Tweet which has been imported to App.js >->->>>>>>>> import Tweet from './Tweet';" likes="100"/>

        </div>
    )
}

export default App;
