        {name: "RAS Eb", message: "Hello  I am Learning React", likes:8}
    ] 
    )

    return(
        <div className="app">
            {users.map(user =>(
              <Tweet name={user.name} message={user.message} likes={likes}/> 
            ))}
        </div>
    );
}

export default App;
