import React from 'react';
import NavItems from './NavItems';
import About from './About';
import Services from './Services';
import Contact from './Contact';
import {BrowserRouter as Router, Switch} from 'react-router-dom'
 


// const counter = 8; this is just like a variable that doesnt change

//this below is our component that returns JSX to index.html 
function Nav(){

    //this is the state at which our app will be invoked
    //it can be a boolean, a string or int just anything of choice
    // const [isRed, setRed]= useState(false);
    // const [count, setCount] = useState(0);

    //The state Below is an exaple of data that can be taken from an API call and retrieved into this useState() 
    //NB: useState([]) is taking in an array/list [] of data



    return(
        <Router>
            <div>
                <Switch>
                    <Route path='/about' exact component={About} />
                    <Route path='/services' exact component={About} />
                    <Route path='/contactus' exact component={About} />
                </Switch>
            </div>

        </Router>

    );
}


export default Nav;
