import React, {useEffect, useContext} from 'react';
import './App.css';
//import {Link} from 'react-router-dom';

import {CarContext} from './CarContext';  //importing car context


 



//this below is our component that returns JSX to index.html 
function CarInfo({match}){

    useEffect(() => {
    }, []);

    const [cars] = useContext(CarContext);
    const selectedCar = match.params.id;

    for (let items = 0; items < cars.length; items++) {

        const mota_one = cars[items]

        if (mota_one["id"] === selectedCar) {
            return(
                <div >
                    <h1>CAR INFO {selectedCar}</h1>
                
                </div>
            );
        }else {
            return(
                <div >
                    <h3>CAr no available</h3>
                </div>
            );
        }

    };

    
}



export default CarInfo;
